mapper.select:
SELECT COUNT(*)
FROM USER_INFO
WHERE USER_ID = #{user_id}

SELECT COUNT(*)
FROM USER_INFO
WHERE TEL = #{user_tel}

SELECT USER_NUM
FROM USER_INFO
WHERE USER_ID = #{user_id}
 AND CRYPTPACK.DECRYPT(USER_PW, #{user_pw}) = #{user_pw}

SELECT USER_ID
FROM USER_INFO
WHERE USER_NAME = #{user_name}
 AND TEL = #{tel}

SELECT COUNT(USER_PW)
FROM USER_INFO
WHERE USER_ID = #{user_id}
 AND TEL = #{tel}

mapper.update:
UPDATE USER_INFO
SET USER_PW = '1234'
WHERE USER_ID = #{user_id}
 AND TEL = #{tel}
